class Kin:
    def __init__(self, kin):
        if not kin:
            raise ValueError("Missing kin number")
        if kin < 1 or kin > 260:
            raise ValueError("Invalid kin number")

        self.kin = kin

        self.tone = kin % 13
        if self.tone == 0:
            self.tone = 13


        self.seal = SEALS[kin % 20]


        self.color = COLORS[kin % 4]


        self.signature = f"{self.color} {TONES[self.tone -1]} {self.seal}"

    def __str__(self):
        return f"Kin : {self.kin} \n{self.signature} \nor \n{self.tone} {self.seal}"

